mapper.insert:

SELECT CURRVAL('board_bno_seq')

INSERT INTO board 
	(
		title
		, writer 
		, contents
	)
VALUES
	(
		#{title}
		, #{writer}
		, #{contents}
	)

INSERT INTO file_board 
	(
		bno
		, org_fname
		, save_fname
		, fpath
	)
VALUES
	(
		#{bno}
		, #{org_fname}
		, #{save_fname}
		, #{fpath}
	)

mapper.update:
UPDATE 
	board
SET
	title = #{title}
	, contents = #{contents}
	, writer = #{writer}
WHERE
	bno = #{bno}
